bool Dictionary::isInDictionary (string keyWord) {

	findWord = dicWords.find (keyWord);

	if ( findWord == dicWords.end() ) {
		//cout << "not found";
		return false;
	}
	else {
		//cout << findWord->first << " is " << findWord->second << endl;
		return true;
	}
}

int Dictionary::isTestTextInDictionary (string testText) {
	int cmpTextLen = 0;
	//Try in Dictionary 2
	for( hashmap::const_iterator iteratorWord = dicWords.begin(), e = dicWords.end(); iteratorWord != e; ++iteratorWord ) {
		string dicWord = iteratorWord->first;
		int lenWord = iteratorWord->second;
		int lenTestText = testText.length();

		cmpTextLen = (lenWord < lenTestText)? lenWord:lenTestText;

		//cout << "Comparing " << dicWord << " and " << testText << " for " << cmpTextLen << " characters" << endl;
		if(strncmp(&dicWord[0], &testText[0], cmpTextLen) == 0) {
			cout << "Found a match from Dictionary 2" << endl;
			return cmpTextLen;
		}
		else {
			cmpTextLen = 0;
		}
	}

	//Try in Dictionary1

	if (dicString.find(testText) != std::string::npos) {
		cmpTextLen = testText.length();
		std::cout << "Found a match from Dictionary 1" << '\n';
	}


	return cmpTextLen;

}

void Dictionary::listDictionary () {
	cout << endl << "Dictionary words:" << endl;
	for( hashmap::const_iterator i = dicWords.begin(), e = dicWords.end(); i != e; ++i ) {
		cout << i->first << " -> " << i->second /*<< " (hash = " << hashFunc( i->first )  << ")" */ << endl;
	}
}

void Dictionary::initHashMap() {
	int numWords = sizeof(listWords)/sizeof(listWords[0]);
	int lenWord = 0;
	cout << "Dictionary size: " << numWords << endl;
	for (int i=0;i<numWords;i++) {
		lenWord = listWords[i].length();

		if(maxLenWords < lenWord) {
			maxLenWords = lenWord;
		}
		if(minLenWords > lenWord) {
			minLenWords = lenWord;
		}
		dicWords[listWords[i]] = lenWord;
	}
}

int Dictionary::getMaxLenWords () {
	return maxLenWords;
}

int Dictionary::getMinLenWords () {
	return minLenWords;
}
